import { usestate ,useRef } from "react";
import { useNavigate } from "react-router-dom";
import '../styles/Login.css';

function Login() {
  const [data, setdata] =usestate({});
  const email = useRef();
  const pass = useRef();
  const navigate = useNavigate();
  
  function handleSubmit(e) {
    e.preventDefault();
    const a = email.current.value;
    const b = pass.current.value;

    if (a === "" || b === "") {
      window.alert("Please enter credentials");
    } else if (a === "abc@gmail.com" && b === "abc123") {
      navigate("/home");
    } else {
      alert("Wrong credentials");
    }
  }

  return (
    <div className="login-container">
      {/* Background Video */}
      <video autoPlay muted loop className="bg-video">
        <source src="https://www.pexels.com/download/video/853800/" type="video/mp4" />
       
      </video>
      <div className="overlay"></div>

      {/* Login Form */}
      <form className="login-form" onSubmit={handleSubmit}>
        <h1>Welcome Back</h1>
        <input type="text" placeholder="Enter email" ref={email} />
        <input type="password" placeholder="Password" ref={pass} />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
