import React from 'react';
import '../styles/AnimatedCartButton.css';

function AnimatedCartButton({ onAddToCart, theme = 'white' }) {
  const handleClick = () => {
    if (onAddToCart) {
      onAddToCart();
    }
  };

  return (
    <button 
      className={`add-to-cart ${theme}`}
      onClick={handleClick}
    >
      <span>Add to cart</span>
    </button>
  );
}

export default AnimatedCartButton;
