import React, { useEffect, useState } from 'react';
import { ShoppingCart, Heart } from 'lucide-react';
import '../styles/APIFetch.css'

function APIFetch() {
  const [api, setApi] = useState([]);
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [showCart, setShowCart] = useState(false);

  useEffect(() => {
    fetch('https://fakestoreapi.com/products/category/electronics')
      .then((response) => response.json())
      .then((data) => setApi(data));
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  const addToWishlist = (product) => {
    if (!wishlist.find(item => item.id === product.id)) {
      setWishlist([...wishlist, product]);
    }
  };

  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  const removeFromWishlist = (productId) => {
    setWishlist(wishlist.filter(item => item.id !== productId));
  };

  return (
    <div className="dark-theme-container">
      {/* Header */}
      <header className="header">
        <div className="header-content">
          <h1>CLOTH STORE</h1>
          <div className="header-icons">
            <button className="cart-btn" onClick={() => setShowCart(!showCart)}>
              <ShoppingCart size={24} />
              <span className="badge">{cart.length}</span>
            </button>
            <button className="wishlist-btn">
              <Heart size={24} />
              <span className="badge">{wishlist.length}</span>
            </button>
          </div>
        </div>
      </header>

      {/* Cart Sidebar */}
      {showCart && (
        <div className="cart-sidebar">
          <div className="cart-header">
            <h2>Your Cart</h2>
            <button onClick={() => setShowCart(false)} className="close-btn">✕</button>
          </div>
          {cart.length === 0 ? (
            <p className="empty-msg">Cart is empty</p>
          ) : (
            <div className="cart-items">
              {cart.map((item, index) => (
                <div key={index} className="cart-item">
                  <img src={item.image} alt={item.title} />
                  <div className="cart-item-details">
                    <h4>{item.title.substring(0, 30)}...</h4>
                    <p>${item.price.toFixed(2)}</p>
                  </div>
                  <button onClick={() => removeFromCart(index)} className="remove-btn">Remove</button>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Products Grid */}
      <main className="main-container">
        <div className="products-grid">
          {api.map((data) => {
            const inWishlist = wishlist.find(item => item.id === data.id);
            return (
              <div key={data.id} className="product-card">
                <div className="product-image">
                  <img src={data.image} alt={data.title} />
                </div>
                <div className="product-info">
                  <h3>{data.title.substring(0, 50)}...</h3>
                  <p className="price">${data.price.toFixed(2)}</p>
                  <p className="description">{data.description.substring(0, 80)}...</p>
                  <div className="button-group">
                    <button 
                      className="add-cart-btn"
                      onClick={() => addToCart(data)}
                    >
                      <ShoppingCart size={18} /> Add to Cart
                    </button>
                    <button 
                      className={`wishlist-icon-btn ${inWishlist ? 'active' : ''}`}
                      onClick={() => inWishlist ? removeFromWishlist(data.id) : addToWishlist(data)}
                    >
                      <Heart size={20} fill={inWishlist ? 'currentColor' : 'none'} />
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </main>
    </div>
  );
}

export default APIFetch;
