import React, { useState, useEffect } from 'react'
import Navbar from './Navbar'
import '../styles/Users.css'
import { useNavigate } from 'react-router-dom'

function Users() {
  const [loggedInUser, setLoggedInUser] = useState(null);
  const [allUsers, setAllUsers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Get logged in user from localStorage
    const user = localStorage.getItem('loggedInUser');
    if (user) {
      setLoggedInUser(JSON.parse(user));
    } else {
      navigate('/');
    }

    // Get all users from localStorage or initial JSON
    const saved = localStorage.getItem('appUsers');
    if (saved) {
      setAllUsers(JSON.parse(saved));
    } else {
      fetch('/users.json')
        .then(res => res.json())
        .then(data => setAllUsers(data.users))
        .catch(err => console.error("Error loading users:", err));
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('loggedInUser');
    navigate('/');
  };

  if (!loggedInUser) {
    return <div><Navbar /></div>;
  }

  return (
    <>
      <Navbar />
      <div className="users-container">
        
        {/* Current Logged In User Profile */}
        <section className="current-user-section">
          <h2>Your Profile</h2>
          <div className="user-profile-card">
            <img src={loggedInUser.image} alt={loggedInUser.name} className="profile-image" />
            <div className="profile-info">
              <h3>{loggedInUser.name}</h3>
              <p><strong>Username:</strong> {loggedInUser.username}</p>
              <p><strong>Email:</strong> {loggedInUser.email}</p>
              <p><strong>User ID:</strong> {loggedInUser.id}</p>
            </div>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </div>
        </section>

        {/* All Users List */}
        <section className="all-users-section">
          <h2>All Users ({allUsers.length})</h2>
          <div className="users-grid">
            {allUsers.map((user) => (
              <div 
                key={user.id} 
                className={`user-card ${loggedInUser.id === user.id ? 'active' : ''}`}
              >
                <img src={user.image} alt={user.name} />
                <div className="user-card-info">
                  <h4>{user.name}</h4>
                  <p className="username">@{user.username}</p>
                  <p className="email">{user.email}</p>
                  {loggedInUser.id === user.id && (
                    <span className="badge">Current User</span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>
    </>
  )
}

export default Users
